import "./saldoDisponivel.css";
import { useState } from "react";

function SaldoDisponivel ({saldo}){
    const [isOculto, setOculto] = useState(false);

    const toggleOculto = () => {
        setOculto(!isOculto);
    }


    return(
        <section className="saldo-card">
            <h2>Saldo Disponível</h2>
            <section className="linha">
                <button onClick={toggleOculto} className="botaoOcultar">
                    
                    {isOculto ? 'Mostrar Saldo' : 'Ocultar Saldo'}
                </button>
                
                <p>R$ {saldo}</p>
            </section>
        </section>
    )
}

export default SaldoDisponivel;
