import './main.css';
import InputMask from 'react-input-mask';
import { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom'; 
import Apresentacao from '../Apresentacao/apresentacao';

function Main () { 
    const [cpf, setCpf] = useState('');
    return (
        <main className="main">
            <Apresentacao />

            <div className="login">
                <h2>Acesse sua conta</h2>
                <input type="text" placeholder="CPF: _ _ _ . _ _ _ . _ _ _ - _ _" />
                <InputMask
                    // 3. Define a máscara no formato desejado
                    mask="999.999.999-99" 
                    // Caracteres que não são números não serão aceitos
                    maskChar={null} 
                    
                    type="text" 
                    placeholder="CPF: _ _ _ . _ _ _ . _ _ _ - _ _" 
                    
                    // Conecta ao estado do React
                    value={cpf}
                    onChange={(e) => setCpf(e.target.value)}
                >
                    {/* Esta função renderiza o input HTML real com as props da máscara */}
                    {(inputProps) => <input {...inputProps} />}
                </InputMask>
                <input type="password" placeholder="Senha" />
                <p className='avisoBan'>Se não inserir a senha correta em 3 tentativas seu acesso será bloqueado por alguns instantes.</p>
                <button>ENTRAR</button>
                <p>Ainda não possui uma conta?</p>
                <Link to="/cadastro">Cadastre-se</Link>
            </div>
        </main>
    )
}

export default Main
