// src/Componentes/Modais/SaqueModal/SaqueModal.jsx

import "./saqueModal.css"
import MainButton from "../../MainButton/mainButton"; // Assumindo que você tem este componente

function SaqueModal({ show, onClose }) {
    if (!show) {
        return null;
    }

    const handleConfirm = () => {
        // Lógica de confirmação da senha (chamar API Java)
        // ...
        onClose(); // Fechar o modal após a tentativa de confirmação
    };

    return (
        <div className="modal-backdrop" onClick={onClose}>
            {/* O evento onClick é capturado no backdrop, mas o modal-content impede o fechamento acidental */}
            <div className="modal-content" onClick={e => e.stopPropagation()}>
                
                {/* Ícone 'X' ou close (opcional) */}
                <button className="close-button" onClick={onClose}>&times;</button> 
                
                <h2 className="modal-title">Está tudo certo para o seu saque!</h2>
                
                <p className="modal-subtitle">Digite sua senha e confirme novamente</p>
                
                <input 
                    type="password" 
                    placeholder="Senha" 
                    className="modal-input"
                />
                
                {/* Botão de Confirmação */}
                <MainButton
                    texto="CONFIRMAR"
                    onClick={handleConfirm}
                    className="modal-confirm-button"
                />
                
                {/* Aviso de Segurança */}
                <p className="modal-security-warning">
                    Se não inserir a senha correta em 3 tentativas seu acesso será bloqueado por alguns instantes.
                </p>
            </div>
        </div>
    );
}

export default SaqueModal;
