// src/Componentes/MainButton/mainButton.jsx (Corrigido para ser Flexível)

import "./mainButton.css";
import { Link } from "react-router-dom"; // Importe o Link novamente!

function MainButton ({texto, onClick, to, type = "button"}) {
    // Se a prop 'to' for fornecida, renderiza um Link para navegação.
    if (to) {
        return (
            <Link className="mainButton" to={to}>

                {texto}
            </Link>
        );
    }

    return (
        <button 
            className="mainButton" 
            onClick={onClick} 
            type={type} 
        >
            {texto}
        </button>
    );
}

export default MainButton;
