import "./mainButton.css";
// Não precisamos do Link aqui, já que estamos focando na AÇÃO (onClick)

function MainButton ({texto, onClick, type = "button"}) {
    return (
        // ✅ A função onClick agora está no botão, e a ação será executada!
        <button 
            className="mainButton" 
            onClick={onClick} 
            type={type} // Permite definir se é submit ou apenas botão de ação
        >
            {texto}
        </button>
    )
}

export default MainButton;
