import Logo from "./Logo/logo"
import { useAuth } from "../../Contextos/auth"
import './header.css'

const Header = () => {
    // A desestruturação do useAuth é o que requer o AuthProvider no topo
    const { estaLogado, usuario } = useAuth();
    
    // Fallback para evitar erro se 'usuario' for null (caso o Provider não esteja configurado)
    const nomeUsuario = usuario ? usuario.nome : 'Usuário'; 
    const cpfMascarado = usuario ? usuario.cpf_mascarado : '***.***.***-**'; 

    return (
        // O elemento pai deve ser o <header>
        <header className="header">
            <Logo />

            {/* O ternário precisa estar DENTRO do <header> */}
            {estaLogado ? (
                // Bloco 'if' (Usuário Logado)
                <div className="informacoes-usuario">
                    <span>
                        Bem-vindo(a), {nomeUsuario} | CPF: {cpfMascarado}
                    </span>
                    {/* Renderiza o timer SÓ quando logado */}
                    <TimerSessao minutos={5} /> 
                </div>
            ) : (
                // Bloco 'else' (Usuário Deslogado)
                // Retornamos null, ou você pode colocar links de Login/Cadastro aqui
                null
            )}
        </header> 
        // A tag <header> é fechada APÓS o ternário
    );
}
        

export default Header
